mapper.select:
SELECT * FROM articleFile
WHERE id IN

	#{value}

AND articleId = #{param.id}

mapper.update:
UPDATE articleFile
SET regDate = NOW(),		
prefix = #{fileNames[0]},
originFileName = #{fileNames[1]},		
type2 = #{type2}
WHERE id = #{fileId} AND articleId = #{param.id}

mapper.delete:
DELETE FROM articleFile
WHERE id IN

	#{value}

AND articleId = #{param.id}
