mapper.update:
UPDATE `member`
SET emailAuthStatus = TRUE
WHERE email = #{email} AND authKey = #{authKey}

UPDATE `member`
SET delStatus = TRUE
WHERE id = #{loginedMemberId}

UPDATE `member`
SET loginPw = #{tempPw}
WHERE loginId = #{loginId} AND email = #{email}

UPDATE `member`
SET loginPw = #{loginPw}
WHERE id = #{loginedMemberId} AND loginPw = #{origin_loginPw}

mapper.select:
SELECT L.*, IFNULL(M.loginId ,'존재하지 않는 사용자') AS writer FROM `letter` AS L LEFT OUTER JOIN `member` AS M ON IF(L.fromMemberId = #{loginedMemberId}, M.id = L.toMemberId, M.id = L.fromMemberId) AND M.delStatus != TRUE WHERE 1=1
AND (toMemberId = #{loginedMemberId} OR fromMemberId= #{loginedMemberId}) AND toMemberId = #{loginedMemberId} AND fromMemberId = #{loginedMemberId}
AND (toMemberId = #{loginedMemberId} OR fromMemberId= #{loginedMemberId})
ORDER BY L.id DESC LIMIT #{startNum}, #{perPageNum}

SELECT COUNT(*) FROM letter
WHERE 1=1

	 AND (toMemberId = #{loginedMemberId} OR fromMemberId= #{loginedMemberId}) 
	 AND toMemberId = #{loginedMemberId} 
	 AND fromMemberId = #{loginedMemberId} 


	AND (toMemberId = #{loginedMemberId} OR fromMemberId= #{loginedMemberId})

SELECT * FROM letter
WHERE ( (toMemberId = #{loginedMemberId} AND fromMemberId = #{memberId}) OR (toMemberId = #{memberId} AND fromMemberId = #{loginedMemberId}) )

	AND id > #{finalLetterId}

mapper.update:
UPDATE letter
SET viewStatus = TRUE
WHERE toMemberId = #{loginedMemberId} AND fromMemberId = #{memberId}

	AND id > #{finalLetterId}
