mapper.update:
UPDATE `member`
SET emailAuthStatus = TRUE
WHERE email = #{email} AND authKey = #{authKey}

UPDATE `member`
SET delStatus = TRUE
WHERE id = #{loginedMemberId}

UPDATE `member`
SET loginPw = #{tempPw}
WHERE loginId = #{loginId} AND email = #{email}

UPDATE `member`
SET loginPw = #{loginPw}
WHERE id = #{loginedMemberId} AND loginPw = #{origin_loginPw}

UPDATE letter
SET delStatus = TRUE
WHERE id = #{id}
